'use strict';

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

module.exports = function(db) {
  var BillingPlan = new Schema({
    name: String,
    planid: String,
    status: String,
    price: Number, //in cents
    minfrequency: String,
    apiallowance: Number,
    realtimecalls: Number,
    hascrawler: Boolean,
    hasalerts: Boolean,
    hasprivate: Boolean,
    haspostauth: Boolean,
    haspdf: Boolean,
    supporttime: Number //in days
  });

  db.model('BillingPlan', BillingPlan);
  return db.model('BillingPlan');
};