'use strict';

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

module.exports = function(db) {
  var CallSchema = new Schema({
    apiid: String,
    apikey: String,
    realtime: Boolean,
    date: Date,
    fromapp: Boolean,
    fromblock: Boolean,
    sochi: Boolean,
    papi: String,
    client: String,
    ip: String
  });

  db.model('Call', CallSchema);
  return db.model('Call');
};