'use strict';

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// TODO create index on apiid
module.exports = function(db) {
  var CrawlUrlSchema = new Schema({
    apiid: String,
    url: String,
    idx: Number
  });

  db.model('CrawlUrl', CrawlUrlSchema);
  return db.model('CrawlUrl');
};