'use strict';

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

module.exports = function(db) {
  var InviteSchema = new Schema({
    email: String,
    token: String,
    requestedon: Date,
    sent: Boolean,
    senton: Date,
    joined: Boolean,
    joinedon: Date
  });

  db.model('Invite', InviteSchema);
  return db.model('Invite');
};