'use strict';

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

module.exports = function(db) {
  var RTCallSchema = new Schema({
    a: String,                  //apiid
    u: Schema.Types.ObjectId,   //userid
    t: String,                  //type
    d: Date                     //date
  });

  db.model('RTCall', RTCallSchema);
  return db.model('RTCall');
};