'use strict';

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

module.exports = function(db) {
  var SupportTicketSchema = new Schema({
    name: String,
    email: String,
    type: String,
    typeName: String,
    ticketid: String,
    message: String,
    createdon: Date,
    resolved: Boolean
  });

  db.model('SupportTicket', SupportTicketSchema);
  return db.model('SupportTicket');
};